descriptor.templateCss:
.card {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-clip: border-box;
    border: 0 solid rgba(0, 0, 0, 0.125);
    border-radius: 0.25rem;
    width : 100%;
    height: 100%;
    font-family: "Times New Roman", Times, serif;

}

.card-header {
    padding: 0.75rem 1.25rem;
    border-top-left-radius: 0.25rem;
    border-top-right-radius: 0.25rem;
    color: black;
    font-weight: bold;
    align-items: center;
    display: flex;
    justify-content: space-between;
    height: 30%;
}

.card-body {
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
    border-bottom-left-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    width:100%;
    height:100%;
}

.status-text {
    font-size: 1em;
    font-weight: normal;
    color: white;
    margin-left: 10px;
}

.nav-button {
    background-color: rgba(255, 255, 255, 0.2);
    border: none;
    color: white;
    padding: 8px 16px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 14px;
    margin: 4px 2px;
    transition-duration: 0.4s;
    cursor: pointer;
    border-radius: 4px;
}

.nav-button:hover {
    background-color: rgba(255, 255, 255, 0.3);
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.cardContainer = self.ctx.$container;
    self.createCard();
}

self.createCard = function() {
    var dataSource = self.ctx.datasources[0];
    var dataKey = dataSource.dataKeys[0];
    var card = $('<div>').addClass('card');
    var cardHeader = $('<div>').addClass('card-header');
    var cardBody = $('<div>').addClass('card-body');
    
    let display = self.ctx.settings.headerVisible ? self.ctx.settings.headerVisible ? "none" : "block" : 'block';
    cardHeader.css('display', display);
    
    // Create header with name and status
    var headerText = $('<span>').text(self.ctx.widgetConfig.title);
    headerText.css({
        'fontSize': '1.3rem',
        'color': self.ctx.widgetConfig.settings.fontColorHeader
    });
    var statusText = $('<span>').addClass('status-text').hide();
    statusText.css('color', self.ctx.widgetConfig.settings.fontColorStatus);
    cardHeader.append(headerText, statusText);

    var iconContainer = $('<div>').css({
        height: '80px',
        width: '80px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: '2rem',
        marginRight: '1rem',
        color: self.ctx.widgetConfig.settings.fontColorIcon
    });
    var icon = $('<i>').addClass(self.ctx.widgetConfig.settings.icon);
    iconContainer.append(icon);
    
    var textContainer = $('<div>').css({
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        flex: 1,
    });
    var valueLabel = $('<label>').css({
        fontSize: '2rem',
        fontWeight: 'bold',
        color: self.ctx.widgetConfig.settings.fontColorValue
    });
    var descriptionLabel = $('<label>').css({
        fontSize: '15px',
        color: self.ctx.widgetConfig.settings.fontColorTextValue
    });
    textContainer.append(valueLabel, descriptionLabel);

    var flexContainer = $('<div>').css({
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        height: '100%',
        color: self.ctx.widgetConfig.settings.fontColor
    }).append(iconContainer, textContainer);

    // Check if the button should be visible
    if (self.ctx.widgetConfig.settings.buttonVisible) {
        var navButton = $('<button>')
            .addClass('nav-button')
            .text(self.ctx.widgetConfig.settings.buttonText || 'Details >')
            .css({
                'color': self.ctx.widgetConfig.settings.fontColorDetail,
                'background-color' : self.ctx.widgetConfig.settings.buttonBgColor
                
            })
            .on('click', function() {
                self.onButtonClick();
            });
        flexContainer.append(navButton);
    }

    cardBody.append(flexContainer);
    card.append(cardHeader, cardBody);

    self.ctx.card = card;
    self.ctx.valueLabel = valueLabel;
    self.ctx.descriptionLabel = descriptionLabel;
    self.ctx.statusText = statusText;
    self.ctx.cardHeader = cardHeader;
    self.ctx.cardBody = cardBody;
    self.ctx.cardContainer.append(card);
}
self.onButtonClick = function() {
    var descriptors = self.ctx.actionsApi.getActionDescriptors('buttonClick');
    if (descriptors.length) {
        var descriptor = descriptors[0];
        if(descriptor.type == "openDashboardState"){
            self.ctx.dashboard.stateController.openState(descriptor.targetDashboardStateId, false, false);
        }

    } else {
        console.log('No action descriptors found for buttonClick');
    }
}


self.onDataUpdated = function() {
    if (self.ctx.data && self.ctx.data.length > 0) {
        
        var isAliveData = self.ctx.data.find(d => d.dataKey.name === 'IsAlive');
        var lastSeenData = self.ctx.data.find(d => d.dataKey.name === 'LastSeenAlive');
        var mainData = self.ctx.data[0]; // Assuming the first dataKey is the main data to display

        var isOnline = self.checkOnlineStatus(isAliveData);
        self.updateOnlineStatus(isOnline, lastSeenData);

        if (mainData && mainData.data && mainData.data.length > 0) {
            var firstDataPoint = mainData.data[0];
            var value = firstDataPoint[1];
            var textValue;

            if (self.isNumber(value)) {
                var decimals = self.ctx.decimals;
                var units = self.ctx.units;
                if (mainData.dataKey.decimals || mainData.dataKey.decimals === 0) {
                    decimals = mainData.dataKey.decimals;
                }
                if (mainData.dataKey.units) {
                    units = mainData.dataKey.units;
                }
                textValue = self.ctx.utils.formatValue(value, decimals, units, true);
                self.updateCardColors(value);
                self.ctx.descriptionLabel.text(textValue);

            } else {
                textValue = value == 'true' ? 'On' : 'Off';
                self.updateCardColors(value);
                self.ctx.valueLabel.text(textValue);

            }
            
        }
        
        // // Update description label
        // if (self.ctx.descriptionLabel) {
        //     self.ctx.descriptionLabel.text(self.ctx.settings.descriptionLabel || '');
        // }
    }
}

self.actionSources = function() {
    return {
        'buttonClick': {
            name: 'Button Click',
            multiple: false
        }
    };
}

self.isNumber = function(n) {
    return !isNaN(parseFloat(n)) && isFinite(n);
}

self.checkOnlineStatus = function(isAliveData) {
    if (!isAliveData) {
        return false;
    }

    return isAliveData.data[isAliveData.data.length - 1][1];
}

self.updateOnlineStatus = function(isOnline, lastSeenData) {
    var settings = self.ctx.settings;
    if (isOnline) {
        self.ctx.statusText.hide();
        self.ctx.statusText.text('Online').show();

        self.ctx.cardHeader.css('background-color', settings.onlineColor || '#00ff67');
    } else {
        var lastSeenTime = lastSeenData ? lastSeenData.data[lastSeenData.data.length - 1][1] : null;
        var formattedLastSeen = lastSeenTime ? self.formatDate(lastSeenTime) : 'Unknown';
        self.ctx.statusText.text('Last seen: ' + formattedLastSeen).show();
        self.ctx.cardHeader.css('background-color', settings.offlineColor || '#ff6573');
    }
}


self.updateCardColors = function(value) {  
    var color;
    var settings = self.ctx.settings;
    
    if (self.isNumber(value)) {
        color = settings.defaultColor || '#808080'; // Default color if no range matches
        
        for (var i = 0; i < settings.valueRanges.length; i++) {
            var range = settings.valueRanges[i];
            if (value >= range.minValue && value <= range.maxValue) {
                console.log(range)
                color = range.color;
                self.ctx.valueLabel.text(range.text);
                break;
            }
        }
    } else {
        color = value == 'true' ? (settings.onColor || '#00ff67') : (settings.offColor || '#ff6573');
    }
    
    self.ctx.cardBody.css('background-color', color);
}


self.onResize = function() {
    // Resize logic if needed
}

self.onDestroy = function() {
    // Clean up logic if needed
}


self.formatDate = function(dateString) {
    var date = new Date(dateString);
    var year = date.getFullYear();
    var month = (date.getMonth() + 1).toString().padStart(2, '0');
    var day = date.getDate().toString().padStart(2, '0');
    var hours = date.getHours().toString().padStart(2, '0');
    var minutes = date.getMinutes().toString().padStart(2, '0');
    
    return `${year}/${month}/${day} ${hours}:${minutes}`;
}

