descriptor.controllerScript:
self.onInit = function() {
    function updateOverlay(id, isAlive, timestamp) {
        const overlay = document.getElementById('overlay' + id);
        const infoEl = document.getElementById('info' + id).querySelector('span');
        if (isAlive) {
            overlay.style.filter = 'brightness(100%) sepia(100%) hue-rotate(50deg) saturate(200%) contrast(110%)';
        } else {
            overlay.style.filter = 'brightness(100%) sepia(100%) hue-rotate(-50deg) saturate(200%) contrast(110%)';
        }
        infoEl.textContent = 'Updated at ' + timestamp;
    }
    function onInit() {
        updateOverlay(1, true, '3:17 PM'); // this is cctv
        updateOverlay(2, false, '3:17 PM'); // this is ipspeaker
        updateOverlay(3, false, '3:17 PM'); //this is panic button
    }
    self.ctx.$scope.color = self.ctx.widgetConfig.settings.fontColor ? self.ctx.widgetConfig.settings.fontColor : "red"

    onInit();
};

self.onDataUpdated = function() {
    var data = self.ctx.data;
    if (data != null) {
        let devices = {};
        console.log("Total data entries:", data.length);

        self.ctx.data.forEach(function(e) {
            let deviceName = e.datasource.name;
            let deviceType = deviceName.split('-')[0];
            let deviceId;

            switch(deviceType) {
                case 'CCTV':
                    deviceId = 1;
                    break;
                case 'IPSpeaker':
                    deviceId = 2;
                    break;
                case 'PanicButton':
                    deviceId = 3;
                    break;
                default:
                    console.log("Unknown device type:", deviceType);
                    return; // Skip this iteration
            }

            if (!devices[deviceName]) {
                devices[deviceName] = { id: deviceId, isAlive: false, lastSeen: '' };
            }

            if (e.dataKey.label.startsWith("IsAlive")) {
                devices[deviceName].isAlive = e.data[0][1];
            } else if (e.dataKey.label.startsWith("LastSeenAlive")) {
                let date = new Date(e.data[0][1]);
                devices[deviceName].lastSeen = date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true });
            }
        });

        console.log("Processed devices:", devices);

        for (let device in devices) {
            updateOverlay(devices[device].id, devices[device].isAlive, devices[device].lastSeen);
        }
    } else {
        console.log("No data received");
    }
};

self.onResize = function() {
    console.log("Resize event triggered");
};

self.onDestroy = function() {
    console.log("Destroying component");
};

function updateOverlay(id, isAlive, timestamp) {
    console.log("Updating overlay:", id, isAlive, timestamp);
    const overlay = document.getElementById('overlay' + id);
    const infoEl = document.getElementById('info' + id).querySelector('span');
    if (isAlive) {
        overlay.style.filter = 'brightness(100%) sepia(100%) hue-rotate(50deg) saturate(200%) contrast(110%)';
    } else {
        overlay.style.filter = 'brightness(100%) sepia(100%) hue-rotate(-50deg) saturate(200%) contrast(110%)';
    }
    infoEl.textContent = 'Updated at ' + timestamp;
}

