self.onInit = function() {
    const { $scope, settings } = self.ctx;
    
    // Generate a unique ID for the chart container
    $scope.id = 'chart-container-' + Math.floor(Math.random() * 10000);
    $scope.cardId = 'card-container-' + Math.floor(Math.random() * 10000);

    // Bind settings to scope variables
    $scope.widgetName = settings.title || "Default Title";
    $scope.cardHeaderFontSize = settings.cardHeaderFontSize || "16px";
    $scope.cardHeaderTextColor = settings.cardHeaderTextColor || "#FFFFFF";
    $scope.cardHeaderStatusFontSize = settings.cardHeaderStatusFontSize || "14px";
    $scope.cardStatusTextColor = settings.cardStatusTextColor || "#FFFFFF";
    
    // Additional bindings based on the schema
    $scope.fontColorHeader = settings.fontColorHeader || "#FFFFFF";
    $scope.fontColorStatus = settings.fontColorStatus || "#FFFFFF";
    $scope.fontColorValue = settings.fontColorValue || "#FFFFFF";
    $scope.fontColorTextValue = settings.fontColorTextValue || "#FFFFFF";
    $scope.fontColorDetail = settings.fontColorDetail || "#FFFFFF";
    
    $scope.buttonText = settings.buttonText || "Details >>>";
    $scope.buttonVisible = settings.buttonVisible ? settings.buttonVisible ? "none" : "block" : 'block';
    $scope.buttonBgColor = settings.buttonBgColor || "red";
    
    $scope.cardBodyBackgroundColor = settings.cardBodyBackgroundColor || "#FFFFFF";
    
    $scope.onlineColor = settings.onlineColor ? settings.onlineColor : '#00ff67';
    $scope.offlineColor = settings.offlineColor ? settings.offlineColor : '#00ff67'

    $scope.headerVisible = settings.headerVisible ? settings.headerVisible ? "none" : "block" : 'block';

    setTimeout(function() {
        $("#" + $scope.cardId + " .nav-button").click(function(){
            self.onButtonClick();
        });
    }, 0);
    
    $scope.biIcon = settings.biIcon || "none";

    // Bind valueRanges
    $scope.valueRanges = settings.valueRanges || [
        { minValue: 0, maxValue: 33, color: "#ff0000", text: "Low" },
        { minValue: 34, maxValue: 66, color: "#00ff00", text: "Medium" },
        { minValue: 67, maxValue: 100, color: "#0000ff", text: "High" }
    ];
    setTimeout(self.initializeChart, 0);

};

self.initializeChart = async function() {
    const ctxScope = self.ctx.$scope;
    const container = document.getElementById(ctxScope.id);

    if (!container) {
        console.error('Container not found with id:', ctxScope.id);
        return;
    }

    const containerWidth = container.clientWidth;
    const containerHeight = container.clientHeight;

    const gaugeRadius = calculateGaugeRadius(containerWidth, containerHeight);
    await self.createChart(gaugeRadius);

    self.chartInitialized = true;

    // If there was a pending data update, process it now
    if (self.pendingDataUpdate) {
        self.updateChartData(self.pendingDataUpdate);
        self.pendingDataUpdate = null;
    }
};


self.createChart = async (gaugeRadius) => {
    var settings = self.ctx.settings;

    try {
        var root = am5.Root.new(self.ctx.$scope.id);
        
        var chart = root.container.children.push(
            am5radar.RadarChart.new(root, {
                panX: false,
                panY: false,
                startAngle: 180,
                endAngle: 360
            })
        );

        var axisRenderer = am5radar.AxisRendererCircular.new(root, {
            innerRadius: -50
        });
        
        axisRenderer.grid.template.setAll({
            stroke: root.interfaceColors.get("background"),
            visible: false,
            strokeOpacity: 0.8
        });
        
        var xAxis = chart.xAxes.push(am5xy.ValueAxis.new(root, {
            maxDeviation: 0,
            min: settings.minValue ? settings.minValue : 0,
            max: settings.maxValue ? settings.maxValue : 100,
            strictMinMax: true,
            renderer: axisRenderer,
        }));
        
        axisRenderer.labels.template.setAll({
            fill: am5.color(settings.chartLabelColor ? settings.chartLabelColor : 0xffffff)
        });
        
        self.ctx.axisDataItem = xAxis.makeDataItem({});

        // Create a container for the pin
        var pinContainer = chart.radarContainer.children.push(am5.Container.new(root, {
            centerX: am5.percent(50),
            centerY: am5.percent(50),
            width: 0,
            height: 0
        }));

        // Create the pin as a Rectangle
       var pin = pinContainer.children.push(am5.Triangle.new(root, {
            width: 10,
            height: 15,
            direction: "top",
            fill: am5.color(0xff0000),
            centerY: am5.percent(0),    // Set pivot point to the tip of the triangle
            centerX: am5.percent(50),
            rotation: 270,
            x: 10  // Position the triangle just outside the chart's edge
        }));

        var bullet = self.ctx.axisDataItem.set("bullet", am5xy.AxisBullet.new(root, {
            sprite: pinContainer
        }));

        xAxis.createAxisRange(self.ctx.axisDataItem);
        
        // For the value and band title
        var label = chart.radarContainer.children.push(am5.Label.new(root, {
            fill: am5.color(settings.chartPinValueColor ? settings.chartPinValueColor : 0xffffff),
            centerX: am5.percent(50),
            textAlign: "center",
            centerY: am5.percent(50),
            fontSize: settings.chartPinValueFontSize
        }));
        
        // Create axis ranges bands
        var bandsData = settings.valueRanges ? settings.valueRanges : [
            { title: "Good", color: "#0000FF", lowScore: 0, highScore: 50 },
            { title: "Moderate", color: "#0f9747", lowScore: 50, highScore: 100 },
            { title: "Unhealthy", color: "#b0d136", lowScore: 100, highScore: 200 },
            { title: "Danger", color: "#ee1f25", lowScore: 200, highScore: 500 }
        ];
        
        bullet.get("sprite").on("rotation", function() {
            var value = self.ctx.axisDataItem.get("value");
            var text = "N/A";
            var bandTitle = "undefined";
            
            if(settings.showDecimal){
                if (typeof value === 'number' && !isNaN(value)) {
                    text = value.toFixed(settings.decimalPlace);
                } 
            } else {
                text = Math.round(value).toString();
            }
                       
            var fill = am5.color(0x000000);
            // Find the appropriate band title based on the current value
            for (var i = 0; i < bandsData.length; i++) {
                if (value >= bandsData[i].lowScore && value <= bandsData[i].highScore) {
                    bandTitle = bandsData[i].title;
                    fill = am5.color(bandsData[i].color);
                    break;
                }
            }

            // Update label with styled text, now both number and band title have the same color
            label.set("text", "[font-size:" + (settings.chartPinValueFontSize * 1.2) + "px font-weight:bold]" + text + "[/]\n[font-size:" + settings.chartPinValueFontSize + "px]" + bandTitle + "[/]");
            label.set("fill", fill);

            pin.animate({
                key: "fill",
                to: fill,
                duration: 500,
                easing: am5.ease.out(am5.ease.cubic)
            });
        });

        chart.bulletsContainer.set("mask", undefined);
        
        am5.array.each(bandsData, function(data) {
            var axisRange = xAxis.createAxisRange(xAxis.makeDataItem({}));
            axisRange.setAll({
                value: data.lowScore,
                endValue: data.highScore
            });
            axisRange.get("axisFill").setAll({
                visible: true,
                fill: am5.color(data.color),
                fillOpacity: 0.8
            });
            // Remove inner labels
            axisRange.get("label").setAll({
                visible: false
            });
        });

        chart.appear(1000, 100);

        root.setThemes([am5themes_Animated.new(root)]);

        self.ctx.chart = chart;
        self.ctx.root = root;

    } catch (error) {
        console.error('Error creating chart:', error);
    }
};

self.onDataUpdated = function() {
    if (!self.chartInitialized) {
        self.pendingDataUpdate = self.ctx.data;
        return;
    }

    self.updateChartData(self.ctx.data);
};

self.updateChartData = function(data) {
    if (data && data.length > 0) {
        var isAliveData = data.find(d => d.dataKey.name === 'IsAlive');
        var lastSeenData = data.find(d => d.dataKey.name === 'LastSeenAlive');
        var mainData = data.filter(dataItem => 
            dataItem.dataKey.name !== "IsAlive" &&
            dataItem.dataKey.name !== "LastSeenAlive")[0];

        var isOnline = self.checkOnlineStatus(isAliveData);
        self.updateOnlineStatus(isOnline, lastSeenData);

        if (mainData && mainData.data && mainData.data.length > 0) {
            var firstDataPoint = mainData.data[0];
            var value = firstDataPoint[1];
            if (self.ctx.axisDataItem) {
                self.ctx.axisDataItem.animate({
                    key: "value",
                    to: value,
                    duration: 500,
                    easing: am5.ease.out(am5.ease.cubic)
                });
            } else {
                console.warn('axisDataItem is undefined. Chart may not be fully initialized.');
            }
        }
    }
};

self.checkOnlineStatus = function(isAliveData) {
    if (!isAliveData) {
        return false;
    }

    return isAliveData.data[isAliveData.data.length - 1][1];
}

self.updateOnlineStatus = function(isOnline, lastSeenData) {
    var $scope = self.ctx.$scope;

    if (isOnline) {
        $scope.isOnline = true;
        $scope.statuss = "Online";
    } else {
        $scope.isOnline = false;
        var lastSeenTime = lastSeenData ? lastSeenData.data[lastSeenData.data.length - 1][1] : null;
        var formattedLastSeen = lastSeenTime ? self.formatDate(lastSeenTime) : 'Unknown';
        $scope.statuss = 'Last seen: ' + formattedLastSeen;
    }
};

self.onResize = function() {
    self.chartInitialized = false;
    self.initializeChart();
};

self.onButtonClick = function() {
    var descriptors = self.ctx.actionsApi.getActionDescriptors('buttonClick');
    if (descriptors.length) {
        var descriptor = descriptors[0];
        if(descriptor.type == "openDashboardState"){
            self.ctx.dashboard.stateController.openState(descriptor.targetDashboardStateId, false, false);
        }

    } else {
        console.log('No action descriptors found for buttonClick');
    }
}

self.actionSources = function() {
    return {
        'buttonClick': {
            name: 'Button Click',
            multiple: false
        }
    };
}

self.formatDate = function(dateString) {
    var date = new Date(dateString);
    var year = date.getFullYear();
    var month = (date.getMonth() + 1).toString().padStart(2, '0');
    var day = date.getDate().toString().padStart(2, '0');
    var hours = date.getHours().toString().padStart(2, '0');
    var minutes = date.getMinutes().toString().padStart(2, '0');
    
    return `${year}/${month}/${day} ${hours}:${minutes}`;
};

function calculateGaugeRadius(containerWidth, containerHeight) {
    const minDimension = Math.min(containerWidth, containerHeight);
    return Math.floor(minDimension * 0.4);
}

