descriptor.controllerScript:
self.onInit = function() {
    var $scope = self.ctx.$scope;
    var settings = self.ctx.settings;

    $scope.poleImage = settings.poleImg;
    $scope.id = "smartPoleWidget_" + Math.floor(Math.random() * 10000);
    
    $scope.smartPoleDetails = settings.smartPoleDetails;

    $scope.companyDetails = settings.companyDetails;
        
    $scope.subscriptionStatus = "Active";
    $scope.subscriptionDetails = settings.subscriptionDetails;

    
    $scope.serviceSensors = settings.serviceSensors;
    $scope.alarmId = settings.alarmId;
    setTimeout(function() {
       $("#" + $scope.id + " .nav-button").click(function(){
            self.onButtonClick();
        });
    }, 0);
    
};

self.onDataUpdated = function() {

    var data = self.ctx.data;
    var settings = self.ctx.settings;
    console.log(self.ctx)
    if (data && data.length > 0) {
        const groupedData = groupDataByDatasource(data);
        // Update smartPoleDetails
        settings.smartPoleDetails.forEach(item => {
            const prefix = item.iconAlt;
            const matchingKey = Object.keys(groupedData).find(key => key.toUpperCase().includes(prefix));       
            

            if (matchingKey) {
                const deviceData = groupedData[matchingKey];
                if (deviceData && deviceData.IsAlive !== undefined) {
                    item.status = deviceData.IsAlive[1] === 'true' ? 'Online' : 'Offline';
                    item.statusText = deviceData.IsAlive[1] === 'true' ? 'Online' : "Offline (Last Seen : " + formatDate(deviceData.LastSeenAlive[1]) +")"
                }
            }
        });
        
        // Update companyDetails
        settings.companyDetails.forEach(item => {
            const prefix = "CUSTOMER";
            const matchingKey = Object.keys(groupedData).find(key => key.toUpperCase().includes(prefix));
            

            if (matchingKey) {
                const companyData = groupedData[matchingKey];
            
                settings.companyDetails.forEach(item => {
                    const prefix1 = item.labelAlt.toUpperCase();
                    
                    const matchingKey1 = Object.keys(companyData).find(key => 
                        key.toUpperCase().includes(prefix1) || 
                        prefix1.includes(key.toUpperCase())
                    );
                    
                    if (matchingKey1) {
                        const deviceData = companyData[matchingKey1];
                        item.value = deviceData[1];
                    } 
                });
            }
        });

        // Update subscriptionDetails
        settings.subscriptionDetails.forEach(item => {
            const prefix = "CUSTOMER";
            const matchingKey = Object.keys(groupedData).find(key => key.toUpperCase().includes(prefix));

            if (matchingKey) {
                const subData = groupedData[matchingKey];
            
                settings.subscriptionDetails.forEach(item => {
                    const prefix1 = item.labelAlt.toUpperCase();
                    
                    const matchingKey1 = Object.keys(subData).find(key => 
                        key.toUpperCase().includes(prefix1) || 
                        prefix1.includes(key.toUpperCase())
                    );
                    
                    if (matchingKey1) {
                        const deviceData = subData[matchingKey1];
                        item.value = deviceData[1];
                    } 
                });
            }
        });
        
        // Update serviceSensors
        settings.serviceSensors.forEach(item => {
            
            const prefix = "CUSTOMER";
            const matchingKey = Object.keys(groupedData).find(key => key.toUpperCase().includes(prefix));

            if (matchingKey) {
                const companyData = groupedData[matchingKey];
                
                settings.serviceSensors.forEach(item => {
                    const prefix1 = item.labelAlt.toUpperCase();
                    
                    // Find all matching keys
                    const matchingKeys = Object.keys(companyData).filter(key => 
                        key.toUpperCase().includes(prefix1) || 
                        prefix1.includes(key.toUpperCase())
                    );
                    
                    matchingKeys.forEach(matchingKey1 => {
                        const deviceData = companyData[matchingKey1];
                        if (matchingKey1.includes("LastService")) {
                            item.lastService = deviceData[1];
                        }
                        if (matchingKey1.includes("NextService")) {
                            item.nextService = deviceData[1];
                        }
                    });
                });
            }
        });
        // Trigger change detection
        if (self.ctx.detectChanges) {
            self.ctx.detectChanges();
        }
    }
};

self.onResize = function() {
    // Implement resize logic if needed
};

self.onDestroy = function() {
    // Add any cleanup code here
};
self.onButtonClick = function() {
    var descriptors = self.ctx.actionsApi.getActionDescriptors('buttonClick');
    console.log(descriptors)
    if (descriptors.length) {
        var descriptor = descriptors[0];
        if(descriptor.type == "openDashboardState"){
            self.ctx.dashboard.stateController.openState(descriptor.targetDashboardStateId, false, false);
        }

    } else {
        console.log('No action descriptors found for buttonClick');
    }
}

self.actionSources = function() {
    return {
        'buttonClick': {
            name: 'Button Click',
            multiple: false
        }
    };
}

function groupDataByDatasource(data) {
    const groupedData = {};
    data.forEach(item => {
        const sourceName = item.datasource.aliasName;
        const keyName = item.dataKey.name;
        const value = item.data[0];

        if (!groupedData[sourceName]) {
            groupedData[sourceName] = {};
        }

        groupedData[sourceName][keyName] = value;
    });
    return groupedData;
}

function formatDate(dateString) {
    // Parse the ISO 8601 date string
    const date = new Date(dateString);

    // Extract date components
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    // Format the date
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
}

