descriptor.controllerScript:
self.onInit = function() {
    var ctxScope = self.ctx.$scope;
    ctxScope.id = 'chart-container-' + Math.floor(Math
        .random() * 10000);
    self.loadData(); // Load data first
    setTimeout(self.initializeChart, 0);
};

self.loadData = function() {
    let dataList = [];
    if (self.ctx.data && self.ctx.data.length > 0) {
        self.ctx.data.forEach(function(item) {
            if (item && item.data && item.data
                .length > 0 && item.data[0][1]
                ) {
                dataList.push({
                    label: item
                        .datasource
                        .name,
                    value: item.data[0][
                        1
                    ]
                });
            }
        });
    }

    // Sort the dataList based on the value
    dataList.sort((a, b) => a.value - b
    .value); // descending 

    // Get the number of items to display from settings, default to 5 if not set
    var itemsToDisplay = self.ctx.settings
        .itemsToDisplay || 5;
    self.ctx.dataList = dataList.slice(0,
        itemsToDisplay); // Store in ctx for later use
};

self.initializeChart = function() {
    const ctxScope = self.ctx.$scope;
    const container = document.getElementById(ctxScope
        .id);

    if (!container) {
        console.error('Container not found with id:',
            ctxScope.id);
        return;
    }

    self.createChart();
};

self.createChart = function() {
    const ctxScope = self.ctx.$scope;
    var settings = self.ctx.settings;

    try {
        var root = am5.Root.new(ctxScope.id);

        // background color
        root.container.set("background", am5.Rectangle
            .new(root, {
                fill: am5.color(settings
                    .containerBackgroundColor ?
                    settings
                    .containerBackgroundColor :
                    "#fdfdfd"),
                fillOpacity: 0
            }));

        var chart = root.container.children.push(
            am5xy.XYChart.new(root, {
                panX: false,
                panY: false,
                wheelX: "none",
                wheelY: "none"
            })
        );

        // Create Y-axis
        var yAxis = chart.yAxes.push(
            am5xy.CategoryAxis.new(root, {
                categoryField: "label",
                renderer: am5xy.AxisRendererY
                    .new(root, {
                        minGridDistance: 10
                    })
            })
        );

        // label color and font size (yaxis)
        yAxis.get("renderer").labels.template.setAll({
            fill: am5.color(settings
                .yAxisLabelColor ||
                "#000000"),
            fontSize: settings
                .yAxisLabelFontSize || 14
        });

        // Create X-axis
        var xAxis = chart.xAxes.push(
            am5xy.ValueAxis.new(root, {
                renderer: am5xy.AxisRendererX
                    .new(root, {}),
                min: settings.minRange || 0,
                max: settings.maxRange || 100
            })
        );

        // label color and font size (x-axis)
        xAxis.get("renderer").labels.template.setAll({
            fill: am5.color(settings
                .xAxisLabelColor ||
                "#000000"),
            fontSize: settings
                .xAxisLabelFontSize || 14
        });

        // Create series
        var series = chart.series.push(
            am5xy.ColumnSeries.new(root, {
                name: "Series",
                xAxis: xAxis,
                yAxis: yAxis,
                valueXField: "value",
                categoryYField: "label",
                tooltip: am5.Tooltip.new(root, {
                    labelText: "{categoryY}: {valueX}"
                })
            })
        );

        // Set custom colors for bars
        series.columns.template.adapters.add("fill",
            function(fill, target) {
                var index = target.dataItem.index;
                var barColors = settings
                    .barColors || ["#ff0000",
                        "#00ff00", "#0000ff",
                        "#ffff00", "#ff00ff"
                    ];
                if (index < barColors.length) {
                    return am5.color(barColors[
                        index]);
                }
                return fill;
            });

        series.columns.template.setAll({
            tooltipText: "{categoryY}: {valueX}",
            tooltipY: 0,
            strokeOpacity: 0
        });

        // value bar outer stroke
        series.columns.template.setAll({
            strokeWidth: 0
        });

        // Add click event to bars
        series.columns.template.events.on("click",
            function(ev) {
                var dataItem = ev.target.dataItem;
                if (dataItem) {
                    const label = dataItem.get(
                        "categoryY").split("-");

                    switch (label[1]) {
                        case 'GCBCO':
                            self.ctx.dashboard.stateController.openState("smartPoleState", {poleId: "6b8f2e50-f25d-11ee-88af-8396712a5882"}, false);
                            break;
                        case 'IKDMYS':
                            self.ctx.dashboard.stateController.openState("smartPoleState", {poleId: "6b8f2e50-f25d-11ee-88af-8396712a5882"}, false);
                            break;
                        case 'MONSANTO':
                           self.ctx.dashboard.stateController.openState("smartPoleState", {poleId: "6b8f2e50-f25d-11ee-88af-8396712a5882"}, false);
                            break;
                        case 'HIROSE':
                            self.ctx.dashboard.stateController.openState("smartPoleState", {poleId: "6b8f2e50-f25d-11ee-88af-8396712a5882"}, false);
                            break;
                        case 'MARUSHIN':
                            self.ctx.dashboard.stateController.openState("smartPoleState", {poleId: "6b8f2e50-f25d-11ee-88af-8396712a5882"}, false);
                            break;
                        default:
                        alert("This is not assign yet");
                            // code
                    }
                    // console.log("Clicked bar:", {
                    //     label: dataItem.get(
                    //             "categoryY"
                    //             ),
                    //     value: dataItem.get(
                    //         "valueX"),
                    //     color: ev.target
                    //         .get("fill")
                    //         .toString()
                    // });
                }
            });

        // Set initial data
        if (self.ctx.dataList) {
            series.data.setAll(self.ctx.dataList);
            yAxis.data.setAll(self.ctx.dataList);
        }

        // Add chart title
        var title = chart.children.push(am5.Label.new(
            root, {
                text: settings.title ||
                    "Chart Title",
                fontSize: 25,
                fontWeight: "500",
                textAlign: "center",
                x: am5.percent(50),
                y: am5.percent(-5),
                centerX: am5.percent(50),
                paddingTop: 0,
                paddingBottom: 0
            }));

        // Make stuff animate on load
        series.appear();
        chart.appear(1000, 100);

        self.ctx.chart = chart;
        self.ctx.root = root;
    } catch (error) {
        console.error('Error creating chart:', error);
    }
};

self.onDataUpdated = function() {
    self.loadData(); // Reload data
    self.updateChartData();
};

self.updateChartData = function() {
    if (self.ctx.chart && self.ctx.dataList) {
        self.ctx.chart.series.getIndex(0).data.setAll(
            self.ctx.dataList);
        self.ctx.chart.yAxes.getIndex(0).data.setAll(
            self.ctx.dataList);
    }
};

self.onResize = function() {
    self.ctx.detectChanges();
    if (self.ctx.chart) {
        self.ctx.chart.root.resize();
    }
};

self.onDestroy = function() {
    if (self.ctx.root) {
        self.ctx.root.dispose();
    }
};

self.onSettingsChanged = function() {
    self.ctx.detectChanges();
    if (self.ctx.chart) {
        self.ctx.root.dispose();
    }
    self.loadData();
    self.initializeChart();
};

