self.onInit = function() {
    const { $scope, settings } = self.ctx;

    $scope.screenshotId = 'screenshotId-' + Math.floor(Math.random() * 10000);

    self.ctx.$scope.background = self.ctx.widgetConfig.settings.background || "red";
    self.ctx.$scope.buttonText = self.ctx.widgetConfig.settings.buttonText || "DONT CLICK ME";
    self.ctx.$scope.fontSize = self.ctx.widgetConfig.settings.fontSize || '1.2rem';
    self.ctx.$scope.icon = self.ctx.widgetConfig.settings.icon || '';

    setTimeout(function() {
        $('#' + $scope.screenshotId).click(function(event) {
            const divToCapture = $("#tb-main-layout").closest($(".tb-dashboard-page"));
            openFullscreenAndCapture(divToCapture[0]);
        });
    }, 10);
};

self.onDestroy = function() {};

self.onButtonClick = function() {
    var descriptors = self.ctx.actionsApi.getActionDescriptors('buttonClick');
    if (descriptors.length) {
        var descriptor = descriptors[0];
        if (descriptor.type == "openDashboardState") {
            self.ctx.dashboard.stateController.openState(descriptor.targetDashboardStateId, false, false);
        }
    } else {
        console.log('No action descriptors found for buttonClick');
    }
};
function openFullscreenAndCapture(elem) {
    if ($(".needScreenshot").length > 6) {
        alert("Too many elements to capture");
        return;
    }

    function enterFullScreen() {
        if (elem.requestFullscreen) {
            return elem.requestFullscreen();
        } else if (elem.webkitRequestFullscreen) {
            return elem.webkitRequestFullscreen();
        } else if (elem.msRequestFullscreen) {
            return elem.msRequestFullscreen();
        }
        return Promise.resolve();
    }

    if ($(".needScreenshot").length > 0) {
        var divs = $("div.needScreenshot");
        var targetElements = $(".customizedtimeserieschart").closest(".tb-absolute-fill");
        
        for (var i = 0; i < divs.length && i < targetElements.length; i++) {
            
            $(targetElements[i]).replaceWith(divs[i]);  // Clear target and append clone
        }
        
        for (var i = $(".needScreenshot").length; i < targetElements.length; i++) {
            var currentDiv = targetElements[i];
            
                // Find the closest gridster-item ancestor and remove it
                var gridsterItem = currentDiv.closest("gridster-item");
                if (gridsterItem) {
                    gridsterItem.remove();
                }
            
        }
        
    }
    
    // Find the back button
    const $backButton = $('#backButton');
    $backButton.css("display", "none");
    
    $("#" + self.ctx.$scope.screenshotId).closest('.tb-widget-container').css("display", "none");

    enterFullScreen().then(() => {
        // Wait a short time for the fullscreen to be applied
        setTimeout(() => {
            // Capture the main layout
            html2canvas(elem, {
                onclone: function(clonedDoc) {
                    const clonedTimewindow = clonedDoc.querySelector('tb-timewindow.ng-untouched.ng-pristine.ng-valid');
                    if (clonedTimewindow) {
                        // Change the font color to white
                        clonedTimewindow.style.color = 'white';

                        // If needed, you can also change the color of child elements
                        clonedTimewindow.querySelectorAll('*').forEach(el => {
                            el.style.color = 'white';
                        });
                    }
                }
            }).then(canvas => {
                const imgData = canvas.toDataURL('image/png');
                const link = document.createElement('a');
                link.href = imgData;
                link.download = 'myImage.png';
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
            }).finally(() => {
                
                // Close fullscreen after capturing, regardless of success or failure
                closeFullscreen();
                $backButton.css("display", "block");
                $("#" + self.ctx.$scope.screenshotId).closest('.tb-widget-container').css("display", "contents");
                location.reload();
            });
        }, 10); // Adjust this delay if needed
    });
    
}

function closeFullscreen() {
    if (document.exitFullscreen) {
        document.exitFullscreen();
    } else if (document.webkitExitFullscreen) {
        document.webkitExitFullscreen();
    } else if (document.msExitFullscreen) {
        document.msExitFullscreen();
    }
}

