descriptor.controllerScript:
var myChart;
var minMaxTable;

self.onInit = function() {
    const { $scope, $container } = self.ctx;
    $scope.title = self.ctx.settings.title || "Title";
    $scope.subtitle = self.ctx.settings.subtitle || "Subtitle";
    $scope.timeseriesChartId = 'time-container-' + Math.floor(Math.random() * 10000);
    
    // Initialize an object to store checkbox states
    $scope.checkboxStates = {};
    $scope.needScreenshot = false;
    
    const chartData = {
        datasets: self.ctx.data.map(dataItem => ({
            label: dataItem.dataKey.label,
            data: [],
            borderColor: dataItem.dataKey.color,
            fill: false
        }))
    };
    
    const options = {
        maintainAspectRatio: false,
        legend: { display: false },
        scales: {
            xAxes: [{
                type: 'time',
                ticks: {
                    maxRotation: 0,
                    autoSkipPadding: 30,
                    fontColor: '#B6C3DB'
                }
            }],
            yAxes: [{
                ticks: {
                    fontColor: '#B6C3DB'
                }
            }]
        },
        elements: {
            line: {
                tension: 0.1 // Very slight curve for smoother appearance
            },
            point: {
                radius: 1, // Consistently small points
                hoverRadius: 3 // Slightly larger on hover
            }
        }
    };
    
    const canvasElement = $container.find('#myChart')[0];
    const canvasCtx = canvasElement.getContext('2d');
    myChart = new Chart(canvasCtx, {
        type: 'line',
        data: chartData,
        options: options
    });
    
    $container.find("tb-legend").css("display","none");
    self.onResize();
    
    // Add checkbox change event handler
    $container.on('change', 'input[type="checkbox"]', function(event) {
        const datasetIndex = $(this).closest('tr').index();
        self.onCheckboxChange(datasetIndex, event);
    });

    self.onDataUpdated();
};

self.onResize = function() {
    if (myChart) {
        myChart.resize();
    }
    self.ctx.detectChanges();
};

self.onDataUpdated = function() {
    const $scope = self.ctx.$scope;
    $scope.data = [];
    self.ctx.data.forEach((dataItem, i) => {
        const dataset = myChart.data.datasets[i];
        dataset.data.length = 0;
        
        let minValue = Infinity;
        let maxValue = -Infinity;
        let sum = 0;
        let count = 0;
        
        dataItem.data.forEach(tsValuePair => {
            const value = tsValuePair[1];
            dataset.data.push({
                t: tsValuePair[0],
                y: value
            });
            
            minValue = Math.min(minValue, value);
            maxValue = Math.max(maxValue, value);
            sum += value;
            count++;
        });
        
        const avgValue = count > 0 ? sum / count : 0;
        
        // Use stored state or default to false
        const isChecked = $scope.checkboxStates[i] === true;
        $scope.data.push({
            name: dataItem.dataKey.label,
            bgcolor: dataItem.dataKey.color,
            min: minValue.toFixed(2),
            max: maxValue.toFixed(2),
            avg: avgValue.toFixed(2),
            checked: isChecked
        });
    });
    
    const xAxis = myChart.options.scales.xAxes[0].ticks;
    xAxis.min = self.ctx.timeWindow.minTime;
    xAxis.max = self.ctx.timeWindow.maxTime;
    
    myChart.update();
    self.ctx.detectChanges();
};

self.onCheckboxChange = function(index, event) {
    const $scope = self.ctx.$scope;
    const isChecked = event.target.checked;
    $scope.checkboxStates[index] = isChecked;
    $scope.needScreenshot = isChecked;
    //self.toggleDataset(index, isChecked);
    self.ctx.detectChanges();
};

self.toggleDataset = function(index, visible) {
    myChart.data.datasets[index].hidden = !visible;
    myChart.update();
};

self.typeParameters = function() {
    return {
        maxDatasources: -1,
        maxDataKeys: -1
    };
};

