descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.calculatePeriod = calculatePeriod;
    self.ctx.$scope.performAction = performAction;
    self.ctx.$scope.sortBy = sortBy;
    self.ctx.$scope.sortColumn = 'startTime';
    self.ctx.$scope.sortReverse = false;
    self.ctx.$scope.isLoading = true;
    // console.log("Widget initialized");
    checkAndUpdateAlarms();
}

self.onDataUpdated = function() {
    // console.log("Data updated");
    checkAndUpdateAlarms();
}

self.actionSources = function() {
    return {
        'actionCellButton': {
            name: 'widget-action.action-cell-button',
            multiple: true,
            hasShowCondition: true
        },
        'rowClick': {
            name: 'widget-action.row-click',
            multiple: false
        }
    };
}

self.onDestroy = function() {
    // Clean up any resources if necessary
}

function checkAndUpdateAlarms() {
    // console.log("Checking alarms...");
    if (self.ctx.defaultSubscription.alarms) {
        updateAlarm();
    } else {
        // console.log("No alarm data available yet");
        self.ctx.$scope.isLoading = true;
        setTimeout(checkAndUpdateAlarms, 1000);
    }
}

function updateAlarm() {
    var alarmData = self.ctx.defaultSubscription.alarms;
    if (alarmData && alarmData.data && Array.isArray(alarmData.data)) {
        updateDisplayBasedOnAlarms(alarmData);

        console.log(alarmData)
        self.ctx.$scope.alarms = alarmData.data.map(function(e) {
            return {
                startTime: new Date(e.startTs).toLocaleString(),
                endTime: e.endTs ? new Date(e.endTs).toLocaleString() : "N/A",
                source: e.details.Source || "N/A",
                status: e.details.Status || e.status || "N/A",
                message: e.details.Message || "N/A",
                event : e.details.Event || "N/A",
                notificationSent: e.details.NotificationSent || "N/A"
            };
        });
        
        // console.log("Updated alarms:", self.ctx.$scope.alarms);
        updateSortedAlarms();
    } else {
        // console.log("No alarm data available or alarm.data is not an array.");
        self.ctx.$scope.alarms = [];
        self.ctx.$scope.sortedAlarms = [];
    }
    self.ctx.$scope.isLoading = false;
    
    self.ctx.detectChanges();
}

function calculatePeriod(endTime, startTime) {
    if (endTime === "N/A") {
        return "Ongoing";
    }
    
    var start = new Date(startTime);
    var end = new Date(endTime);
    var diff = end - start;
    
    var hours = Math.floor(diff / 3600000);
    var minutes = Math.floor((diff % 3600000) / 60000);
    var seconds = Math.floor((diff % 60000) / 1000);
    
    return hours + "h " + minutes + "m " + seconds + "s";
}

function performAction(alarm) {
    console.log("Action performed on alarm:", alarm);
}

function sortBy(columnName) {
    self.ctx.$scope.sortReverse = (self.ctx.$scope.sortColumn === columnName) ? !self.ctx.$scope.sortReverse : false;
    self.ctx.$scope.sortColumn = columnName;
    updateSortedAlarms();
}

function updateSortedAlarms() {
    self.ctx.$scope.sortedAlarms = self.ctx.$scope.alarms.slice().sort(function(a, b) {
        var aValue = a[self.ctx.$scope.sortColumn];
        var bValue = b[self.ctx.$scope.sortColumn];
        
        if (self.ctx.$scope.sortColumn === 'period') {
            aValue = calculatePeriod(a.endTime, a.startTime);
            bValue = calculatePeriod(b.endTime, b.startTime);
        }
        
        if (aValue < bValue) return self.ctx.$scope.sortReverse ? 1 : -1;
        if (aValue > bValue) return self.ctx.$scope.sortReverse ? -1 : 1;
        return 0;
    });
    
    self.ctx.detectChanges();
}

function updateDisplayBasedOnAlarms(alarmData) {

    // Check if alarmData is an object with a data property
    if (alarmData && Array.isArray(alarmData.data)) {
        alarmData.data.forEach(function(alarm) {
            if (alarm.originatorName) {
                console.log(alarm.originatorName)
                if (alarm.originatorName.includes("IPSpeaker")) {
                    $("#IPSPEAKERID").css('display', "");
                }
                if (alarm.originatorName.includes("CCTV")) {
                    $("#CCTVID").css('display', "");
                }
                if (alarm.originatorName.includes("AIR")) {
                    $("#AIRID").css('display', "");
                }
                if (alarm.originatorName.includes("PanicButton")) {
                    $("#PANICID").css('display', "");
                }
                if (alarm.originatorName.includes("SOLAR")) {
                    $("#SOLARID").css('display', "");
                }
                if (alarm.originatorName.includes("PH")) {
                    $("#PHID").css('display', "");
                }
            }
        });
    } else {
        console.error("Unexpected alarmData format:", alarmData);
    }
}

