descriptor.controllerScript:
self.onInit = function() {
    const { $scope, settings } = self.ctx;
    // Generate a unique ID for the chart container
    $scope.id = 'solar-chart-container-' + Math.floor(Math.random() * 10000);

    $scope.cardId = generateName('solar-chart-container-');
    
    $scope.widgetName = self.ctx.dashboardWidget.customTranslatedTitle;
    
    // Bind settings to scope variables based strictly on the schema
    $scope.fontColorHeader = settings.fontColorHeader || "#FFFFFF";
    $scope.fontColorHeaderStatus = settings.fontColorHeaderStatus || "#FFFFFF";
    $scope.fontColorTextValue = settings.fontColorTextValue || "#FFFFFF";
    $scope.fontColorButtonText = settings.fontColorButtonText || "#FFFFFF";
    
    $scope.buttonText = settings.buttonText || "Default";
    $scope.buttonBgColor = settings.buttonBgColor || "#FFFFFF";
    $scope.cardHeaderFontSize = "1.3vw"

    $scope.buttonVisible = settings.buttonVisible ? "none" : "block";
    $scope.headerVisible = settings.headerVisible  ? "none" : "block";
    
    $scope.onlineColor = settings.onlineColor || '#27e2bf';
    $scope.offlineColor = settings.offlineColor || '#e22741';
    $scope.componentsDetails = settings.componentsDetails;

    $scope.contentBgColor = settings.contentBgColor || "#FFFFFF";
    $scope.biIcon = settings.biIcon || "none";

    setTimeout(function() {
        $("#" + $scope.cardId + " .nav-button").click(function(){
            self.onButtonClick();
        });
    }, 0);
};

self.onDataUpdated = function() {
    var settings = self.ctx.settings;
    var data = self.ctx.data;
    
    settings.componentsDetails.forEach(item => {
        const prefix = item.labelAlt.toUpperCase();
        
        // Find matching dataKey.name in the data array
        const matchingData = data.find(d => 
            d.dataKey && d.dataKey.name && 
            d.dataKey.name.toUpperCase().includes(prefix)
        );
        
        if (matchingData) {
            // Extract the actual data from the matching object
            var value = matchingData.data[0][1];
            if(isNaN(value))
                value = value.toUpperCase()
            item.value = value;
        } else {
            console.warn(`No matching data found for component: ${item.labelAlt}`);
        }
    });
    
    // Trigger change detection
    if (self.ctx.detectChanges) {
        self.ctx.detectChanges();
    }
};
self.checkOnlineStatus = function(isAliveData) {
    if (!isAliveData) {
        return false;
    }

    return isAliveData.data[isAliveData.data.length - 1][1];
}

self.updateOnlineStatus = function(isOnline, lastSeenData) {
    var $scope = self.ctx.$scope;

    if (isOnline) {
        $scope.isOnline = true;
        $scope.statuss = "Online";
    } else {
        $scope.isOnline = false;
        var lastSeenTime = lastSeenData ? lastSeenData.data[lastSeenData.data.length - 1][1] : null;
        var formattedLastSeen = lastSeenTime ? self.formatDate(lastSeenTime) : 'Unknown';
        $scope.statuss = 'Last seen: ' + formattedLastSeen;
    }
};

self.onResize = function() {
    self.chartInitialized = false;
};

self.onButtonClick = function() {
    var descriptors = self.ctx.actionsApi.getActionDescriptors('buttonClick');
    if (descriptors.length) {
        var descriptor = descriptors[0];
        if(descriptor.type == "openDashboardState"){
            self.ctx.dashboard.stateController.openState(descriptor.targetDashboardStateId, false, false);
        }

    } else {
        console.log('No action descriptors found for buttonClick');
    }
}

self.actionSources = function() {
    return {
        'buttonClick': {
            name: 'Button Click',
            multiple: false
        }
    };
}

self.formatDate = function(dateString) {
    var date = new Date(dateString);
    var year = date.getFullYear();
    var month = (date.getMonth() + 1).toString().padStart(2, '0');
    var day = date.getDate().toString().padStart(2, '0');
    var hours = date.getHours().toString().padStart(2, '0');
    var minutes = date.getMinutes().toString().padStart(2, '0');
    
    return `${year}/${month}/${day} ${hours}:${minutes}`;
};

function generateName(prefixName){
    var result = prefixName + Math.floor(Math.random() * 10000)

    if($("#" + result).length>0){
        console.log("repeated " , prefixName)
        generateName(prefixName)
    }else{
        return result
    }
    
}

